import { useState } from 'react';
import { quoteList } from './quoteList.js';

function App() {
  const [index, setIndex] = useState(0);
  const [message, setMessage] = useState(false);

  function handleNextClick() {
    setIndex(index + 1);
  }

  let quote = quoteList[index];
  return (
    <>
    <body style={{
      backgroundColor: 'rgb(255, 227, 194)'
    }}>

      <h1 style={{
        color:'rgb(32, 5, 153)',
        textAlign:'center',
        fontSize: '200%',
        fontFamily:'comic sans ms',
      }}>Let's get some Kanye Quotes!</h1>

      <p style={{
        textAlign:'center'
      }}>
        <img
          src='https://kanye-app-solutions.vercel.app/static/media/kanye-west-kanye.39657b72.gif'
          alt='Kanye West gif'
          style={{
            width:'70%',
            height:'70%',
            borderRadius: '3%'
          }}
        />
      </p>

      <p style={{
        textAlign:'center'
      }}>
        <button 
          onClick={handleNextClick}
          style={{
            background:'rgb(189, 28, 17)',
            color:'white',
            fontFamily:'comic sans ms',
            fontSize: '130%',
            borderRadius:'10px',
            borderColor: 'rgb(207, 207, 207)',
            borderWidth: '5px',
            width:'240px',
            height: '70px'
          }}>
          Generate Quote
        </button>
      </p>

      <p style={{
        textAlign:'center'
      }}>
        <button>
          <i> {quote.quote} </i>
        </button>
      </p>

    </body>
    </>
  );
}

export default App;
